import * as React from "react";
import { themeProduct, ThemeContextProduct } from "../themeProduct";
import AppBar from "@mui/material/AppBar";
import Button from "@mui/material/Button";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Grid from "@mui/material/Grid";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import { useContext } from "react";
import { ThemeProvider } from "@mui/material/styles";
import { theme } from "../services/theme";

import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";



export function MainPage() {


  const data = useContext(ThemeContextProduct);

  return (
    <div>
      <ThemeContextProduct.Provider value={themeProduct}>
        <ThemeProvider theme={theme}>
          <header>
            <Container fixed>
                <AppBar position="static">
                  <Toolbar>
                    <IconButton
                      edge="start"
                      className={classes.menuButton}
                      color="inherit"
                      aria-label="menu"
                    >
                      <MenuIcon />
                    </IconButton>
                    <Typography variant="h6" className={classes.title}>
                      News
                    </Typography>
                    <Button color="inherit">Login</Button>
                  </Toolbar>
                </AppBar>
            </Container>
          </header>
          <main>
            <Box
              sx={{
                bgcolor: "background.paper",
                pt: 8,
                pb: 6,
              }}
            ></Box>
            <Container sx={{ py: 8 }} maxWidth="md">
              {/* End hero unit */}
              <Grid container spacing={4}>
                {data.map((card) => (
                  <Grid item key={card.id} xs={12} sm={6} md={4}>
                    <Card
                      // className={classes.card}
                      sx={{
                        height: "100%",
                        display: "flex",
                        flexDirection: "column",
                      }}
                      style={{ paddingTop: "20px" }}
                    >
                      <CardMedia
                        component="img"
                        sx={{
                          // 16:9
                          pt: "10.25%",
                        }}
                        src={`/imegesGoods/${card.photo}`}
                        alt="random"
                      />
                      <CardContent sx={{ flexGrow: 1 }}>
                        <Typography gutterBottom variant="h5" component="h2">
                          {card.name}
                        </Typography>
                        {/* <Typography>
                            This is a media card. You can use this section to
                            describe the content.
                          </Typography> */}
                      </CardContent>
                      <Typography>Price: {card.price}</Typography>
                      <CardActions>
                        <Button size="small">BUY</Button>
                        <Button size="small">ABOUT</Button>
                      </CardActions>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            </Container>
          </main>
          <footer>
            <Box sx={{ bgcolor: "background.paper", p: 6 }} component="footer">
              <Typography variant="h6" align="center" gutterBottom>
                Footer
              </Typography>
              <Typography
                variant="subtitle1"
                align="center"
                color="text.secondary"
                component="p"
              >
                Something here to give the footer a purpose!
              </Typography>
            </Box>
          </footer>
        </ThemeProvider>
      </ThemeContextProduct.Provider>
    </div>
  );
}
