import * as React from "react";
import { themeProduct, ThemeContextProduct } from "../themeProduct";
import AppBar from "@mui/material/AppBar";
import Button from "@mui/material/Button";
import CameraIcon from "@mui/icons-material/PhotoCamera";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import CssBaseline from "@mui/material/CssBaseline";
import Grid from "@mui/material/Grid";
import Stack from "@mui/material/Stack";
import Box from "@mui/material/Box";
import Toolbar from "@mui/material/Toolbar";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import Link from "@mui/material/Link";
import { useContext } from "react";
import { ThemeProvider } from '@mui/material/styles';
import {theme} from '../services/theme';
import { classes } from "./styleMain";





function Copyright() {
  return (
    <Typography variant="body2" color="text.secondary" align="center">
      {"Copyright © "}
      <Link color="inherit" href="https://mui.com/">
        Your Website
      </Link>{" "}
      {new Date().getFullYear()}
      {"."}
    </Typography>
  );
};


export function MainPage() {

  const  data  = useContext(ThemeContextProduct);


  console.log(data);
  return (
      <div>
        <ThemeContextProduct.Provider value={themeProduct}>
          <ThemeProvider theme={theme}>
            <CssBaseline />
            <AppBar position="relative">
              <Toolbar>
                <CameraIcon sx={{ mr: 2 }} />
                <Typography variant="h6" color="inherit" noWrap>
                  Album layout
                </Typography>
              </Toolbar>
            </AppBar>
            <main>
              {/* Hero unit */}
              <Box
                sx={{
                  bgcolor: "background.paper",
                  pt: 8,
                  pb: 6,
                }}
              >

              </Box>
              <Container sx={{ py: 8 }} maxWidth="md">
                {/* End hero unit */}
                <Grid container spacing={4}>
                  {data.map((card) => (
                    <Grid item key={card.id} xs={12} sm={6} md={4}>
                      <Card
                      className={classes.card}
                        sx={{
                          height: "100%",
                          display: "flex",
                          flexDirection: "column",
                        }}
                      >
                        <CardMedia
                          component="img"
                          sx={{
                            // 16:9
                            pt: "56.25%",
                          }}
                          src={`/imegesGoods/${card.photo}`}
                          alt="random"
                        />
                        <CardContent sx={{ flexGrow: 1 }}>
                          <Typography gutterBottom variant="h5" component="h2">
                            {card.name}
                          </Typography>
                          {/* <Typography>
                            This is a media card. You can use this section to
                            describe the content.
                          </Typography> */}
                        </CardContent>
                        <CardActions>
                          <Button size="small">BUY</Button>
                          <Button size="small">ABOUT</Button>
                        </CardActions>
                      </Card>
                    </Grid>
                  ))}
                </Grid>
              </Container>
            </main>
            {/* Footer */}
            <Box sx={{ bgcolor: "background.paper", p: 6 }} component="footer">
              <Typography variant="h6" align="center" gutterBottom>
                Footer
              </Typography>
              <Typography
                variant="subtitle1"
                align="center"
                color="text.secondary"
                component="p"
              >
                Something here to give the footer a purpose!
              </Typography>
              <Copyright />
            </Box>
            {/* End footer */}
          </ThemeProvider>
        </ThemeContextProduct.Provider>
      </div>
  );
}
